function starttimer(e) {
	var t = e.FlipClock({
			clockFace : "HourlyCounter",
			autoStart : !1,
			language : "ru",
			callbacks : {
				stop : function () {
					$(".message").html("The clock has stopped!")
				}
			}
		});
	t.setTime(2307),
	t.setCountdown(!0),
	t.start()
}
